CREATE PROCEDURE sp_GetColumnMappings
AS
BEGIN
DECLARE @jsonTemp nvarchar(max) = '{ "type": "TabularTranslator", "mappings": X }';
DECLARE @jsonMappings nvarchar(max);

SET @jsonMappings = (
    SELECT ColumnNumber AS [source.ordinal], ColumnName AS [sink.name]
    FROM ColumnMappings
    FOR JSON PATH
);

-- Output final JSON
SELECT REPLACE(@jsonTemp, 'X', @jsonMappings) ColumnMapping
END

IF OBJECT_ID('OrdersNew25may', 'U') IS NOT NULL
    DROP TABLE OrdersNew25may;

CREATE TABLE OrdersNew25may (
    OrderDate datetime,
    ProductKey int,
    Country varchar(100),
    EnglishProductName varchar(100),
    SalesAmount money,
    TaxAmt money
);
